// Github: https://github.com/Tierion/merkle-tools 

var assert = require('assert');
var crypto = require('crypto');
var MerkleTools = require('../merkletools.js');

var treeOptions = {
  hashType: 'sha256' // optional, defaults to 'sha256'
};
// valid hashTypes include all crypto hash algorithms
// such as 'MD5', 'SHA1', 'SHA224', 'SHA256', 'SHA384', 'SHA512'
// as well as the SHA3 family of algorithms
// including 'SHA3-224', 'SHA3-256', 'SHA3-384', and 'SHA3-512'

var merkleTools = new MerkleTools(treeOptions); // treeOptions is optional

var leaves = ["this is leaf1", "this is leaf2", "this is leaf3"]; 


merkleTools.addLeaves(leaves, true); 
console.log("successfully loaded merkleTools: leaf count = " + merkleTools.getLeafCount());

merkleTools.makeTree(); 
console.log("Get tree ready state: " + merkleTools.getTreeReadyState());

var root = merkleTools.getMerkleRoot(); 
console.log("Get merkle root: " + root);
